            "publishedAt": "2024-05-13T09:14:29Z",
            "content": "If you click 'Accept all', we and our partners, including 238 who are part of the IAB Transparency &amp; Consent Framework, will also store and/or access information on a device (in other words, use … [+678 chars]"
        }]

    // async componentDidMount() {
    //     let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=6a8c54565120457189c18d57a5a0cf1b&pageSize=8"
    //     let data = await fetch(url)
    //     let parsedData = await data.json()

    //     this.setState({ article_list: parsedData.articles });

    // }

    constructor() {

        super()
        this.state = {
            article_list: this.articles
        }

    }

    render(props) {
        console.log(this.state.article_list)

        return (
            <div class="">
                {this.state.article_list.map((element) => {
                    return (
                            <div className="card col-md-4" key={element.url} style={{ width: "18rem" }} >
                                <img src={element.urlToImage} className="card-img-top" alt="..." />
                                <div className="card-body">
                                    <h5 className="card-title">{element.title?element.title:""}</h5>
                                    <p className="card-text">{element.description?element.description:""}</p>
                                    <a href={element.url?element.url:""} target="" className="btn btn-primary">Go somewhere</a>
                                </div>
                            </div>
                    );
                })}
            </div>
        )
    }
}
