import React, { Component } from 'react'
export default class NewsCard extends Component {

    constructor() {
        super()
        this.state = {
            article_list: []
        }
    }
    componentDidMount() {
        var url = 'https://newsapi.org/v2/everything?' +
            'q=Apple&' +
            'from=2024-05-19&' +
            'sortBy=popularity&' +
            'apiKey=6a8c54565120457189c18d57a5a0cf1b';

        var req = new Request(url);

        fetch(req)
            // .then(function (response) {
            //     this.setState({ article_list: response })
            //     console.log(this.article_list);
            // })
        .then(data => {
            this.setState({ article_list: data.articles });
            console.log(this.state.article_list); // Correctly log the updated state
        })

    }



render(props) {

    return (

        <div>
            {this.state.article_list.map((element) => {
                return (
                    <div class="col-md-4 mb-4" key={element.url}>
                        <div className="card" style={{ width: "18rem" }} >
                            <img src={element.urlToImage} className="card-img-top" alt="..." />
                            <div className="card-body">
                                <h5 className="card-title">{element.title}</h5>
                                <p className="card-text">{element.description}</p>
                                <a href={element.url} className="btn btn-primary">Go somewhere</a>
                            </div>
                        </div>
                    </div>
                );
            })}
        </div>
    )
}}
